import React, {Component} from 'react';
import { NavLink } from 'react-router-dom';
import {Dropdown} from 'react-bootstrap';

import './NavigationItems.css';

const navItems = [
  { id: 'feed', text: 'Feed', link: '/', auth: true },
  { id: 'my-profile', text: 'My Profile', link: '/posts', auth: true},
  { id: 'my-bucket', text: 'My Bucket', link: '/my-bucket', auth: true},
  { id: 'my-map', text: 'My Map', link: '/my-map', auth: true},
  { id: 'login', text: 'Login', link: '/', auth: false },
  { id: 'signup', text: 'Signup', link: '/signup', auth: false }
];


class navigationItems extends Component {
  state = {
    loaded: false,
    name: null
  }

  getUserName = (userId, token) => {
    console.log(userId);
    fetch('http://localhost:8080/auth/user/' + userId, {
        method: 'GET',
        headers: {
          Authorization: 'Bearer ' + token
        }
      })
      .then(res => {
        if (res.status !== 200 && res.status !== 201) {
          throw new Error('Fetching user name failed!');
        }
        return res.json();
      })
      .then(resData => {
        console.log(resData.userObj.name);
        return resData.userObj.name;
      })
      .catch(err => {
        console.log(err);
      });
    }

  componentDidMount() {
    console.log('in');
    if (!this.state.loaded && this.props.isAuth){
      this.setState({
        loaded: true,
        name:  this.getUserName(this.props.userId, this.props.token)
      })
    }
  }

  render () {
    return ([
        ...navItems.filter(item => item.auth === this.props.isAuth).map(item => (
          <li
            key={item.id}
            className={['navigation-item', this.props.mobile ? 'mobile' : ''].join(' ')}
          >
            <NavLink to={item.link} exact onClick={this.props.onChoose}>
              {item.text}
            </NavLink>
          </li>
        )),
        this.props.isAuth && (
          <li className="navigation-item" key="logout">
            <Dropdown>
              <Dropdown.Toggle variant="Warning" id="dropdown-basic">
                {this.state.name}
              </Dropdown.Toggle>
              <Dropdown.Menu>
                <Dropdown.Item 
                  style={{color: "purple"}} 
                  onClick={this.props.onLogout}>
                  Logout
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </li>
        )]);
  }
}

export default navigationItems;
