import React, {Component} from 'react';
import {Toast} from 'react-bootstrap';

class Toastt extends Component {

  render() {
    const [showB, setShowB] = React.useState(true);
    const toggleShowB = () => setShowB(!showB);
    return (
        <Toast onClose={toggleShowB} show={showB} animation={false}>
          <Toast.Header>
            <strong className="mr-auto">{this.props.user}</strong>
            <small>rating: {this.props.rating}</small>
          </Toast.Header>
          <Toast.Body>{this.props.comment}</Toast.Body>
        </Toast>            
    );
  }
}

export default Toastt;
