import React, { Component } from 'react';

import Image from '../../../components/Image/Image';
import './SinglePost.css';
import { Container, Row, Col } from 'react-bootstrap';


class SinglePost extends Component {
  state = {
    title: '',
    author: '',
    date: '',
    image: '',
    content: '',
    taken_date: '',
    location: '',
    ISO: '',
    shutter_speed: '',
    aperture: '',
    camera: '',
    lens: '',
    equipment: '',
    edit_soft: ''
  };

  componentDidMount() {
    const postId = this.props.match.params.postId;
    fetch('http://localhost:8080/feed/post/' + postId, {
      headers: {
        Authorization: 'Bearer ' + this.props.token
      }
    })
      .then(res => {
        if (res.status !== 200) {
          throw new Error('Failed to fetch status');
        }
        return res.json();
      })
      .then(resData => {
        this.setState({
          title: resData.post.title,
          author: resData.post.creator.name,
          image: 'http://localhost:8080/' + resData.post.imageUrl,
          date: new Date(resData.post.createdAt).toLocaleDateString('en-US'),
          content: resData.post.content,
          taken_date: resData.post.taken_date,
          location: resData.post.location,
          ISO: resData.post.ISO,
          shutter_speed: resData.post.shutter_speed,
          aperture: resData.post.aperture,
          camera: resData.post.camera,
          lens: resData.post.lens,
          equipment: resData.post.equipment,
          edit_soft: resData.post.edit_soft,
        });
      })
      .catch(err => {
        console.log(err);
      });
  }

  render() {
    return (
      <section className="single-post">
        <h1>{this.state.title}</h1>
        <h2 className="single-post-border">
          Created by {this.state.author} on {this.state.date}
        </h2>
        <div className="single-post__image">
          <Image contain imageUrl={this.state.image} />
        </div>
        <div className="single-post__info padding">
          <Container>
            <Col>
              <Row><h2>ISO: {this.state.ISO}</h2></Row>
              <Row><h2>Shutter Speed: {this.state.shutter_speed}</h2></Row>
              <Row><h2>Aperture: {this.state.aperture}</h2></Row>
            </Col>
            <Col>
              <Row><h2>Camera: {this.state.camera}</h2></Row>
              <Row><h2>Lens: {this.state.lens}</h2></Row>
              <Row><h2>Equipments: {this.state.equipment}</h2></Row>
              <Row><h2>Post-Editing Softwares Used: {this.state.edit_soft}</h2></Row>
            </Col>

          </Container>
        </div>

        <h2>Shot at {this.state.location} on {this.state.taken_date}</h2>







      </section>
    );
  }
}

export default SinglePost;
