import React, {Component} from 'react';
import {Card, Button} from 'react-bootstrap';
import './pin.css';

class pin extends Component{
    state = {
        cardReveal: false
    };

    Card = () => {
        return (
            <Card style={{ width: '10rem' }}>
                <Card.Img 
                    variant="top" 
                    src={'http://localhost:8080/' + this.props.image}  
                    alt={this.props.title}/>
                <Card.Body>
                    <Card.Title className='title'>{this.props.title}</Card.Title>
                    <Card.Text className='location'>{this.props.location}</Card.Text>
                    <Button mode="flat" className='closeButton' onClick={() => {
                    this.setState({
                            cardReveal: false
                        });
                    }}>
                        Close
                    </Button>
                </Card.Body>
            </Card>
        )
    };

    render() {
        return (
        <div className="pin">
            <img 
                src={"cam_marker.png"} 
                alt='Spot' 
                style={{width: '30px', height:'100%'}}
                onClick={() => {
                    this.setState({
                        cardReveal: (!this.state.cardReveal)
                    });
                }}
                /> 
        {this.state.cardReveal ? this.Card() : null}
        </div>)
    };
};

export default pin;
