import React from 'react';
import {Card, Button} from 'react-bootstrap';
import './pin.css';

const pin = props => (
  <div className="pin">

    <Card hidden style={{ width: '10rem' }}>
        <Card.Img variant="top" src='../../../public/cam_marker.png' />
        <Card.Body>
            <Card.Title>{props.title}</Card.Title>
            <Button variant="primary">Close</Button>
        </Card.Body>
    </Card>
  </div>
);

export default pin;
