import React, { Component } from 'react';
import { Card, Button, Row, Col } from 'react-bootstrap';
import './pin.css';

class pin extends Component {
    state = {
        cardReveal: false
    };

    Card = () => {
        return (
            <Card style={{ width: '10rem' }}>
                <Card.Img
                    variant="top"
                    src={'http://localhost:8080/' + this.props.image}
                    alt={this.props.title} />
                <Card.Body>
                    <Card.Title className='title'>{this.props.title}</Card.Title>
                    <Card.Text className='location'>{this.props.location}</Card.Text>
                    <Row>
                        <Col>
                            <Button size="sm" variant="outline-dark" className='closeButton' onClick={() => {
                                this.setState({
                                    cardReveal: false
                                });
                            }}>
                                Cl
                            </Button>
                        </Col>
                        <Col>
                            <Button size="sm" variant="outline-dark" link={this.props.id}>
                                View
                            </Button>
                        </Col>
                    </Row>
                </Card.Body>
            </Card>
        )
    };

    render() {
        return (
            <div className="pin">
                <img
                    src={"cam_marker.png"}
                    alt='Spot'
                    style={{ width: '30px', height: '100%' }}
                    onClick={() => {
                        this.setState({
                            cardReveal: (!this.state.cardReveal)
                        });
                    }}
                />
                {this.state.cardReveal ? this.Card() : null}
            </div>)
    };
};

export default pin;
