import React, { Component } from 'react';

import Image from '../../../components/Image/Image';
import './SinglePost.css';


class SinglePost extends Component {
  state = {
    title: '',
    author: '',
    date: '',
    image: '',
    content: '',
    taken_date: '',
    location: '',
    ISO: '',
    shutter_speed: '',
    aperture: '',
    camera: '',
    lens: '',
    equipment: '',
    edit_soft: ''
  };

  componentDidMount() {
    const postId = this.props.match.params.postId;
    fetch('http://localhost:8080/feed/post/' + postId, {
      headers: {
        Authorization: 'Bearer ' + this.props.token
      }
    })
      .then(res => {
        if (res.status !== 200) {
          throw new Error('Failed to fetch status');
        }
        return res.json();
      })
      .then(resData => {
        this.setState({
          title: resData.post.title,
          author: resData.post.creator.name,
          image: 'http://localhost:8080/' + resData.post.imageUrl,
          date: new Date(resData.post.createdAt).toLocaleDateString('en-US'),
          content: resData.post.content,
          taken_date: resData.post.taken_date,
          location: resData.post.location,
          ISO: resData.post.ISO,
          shutter_speed: resData.post.shutter_speed,
          aperture: resData.post.aperture,
          camera: resData.post.camera,
          lens: resData.post.lens,
          equipment: resData.post.equipment,
          edit_soft: resData.post.edit_soft,
        });
      })
      .catch(err => {
        console.log(err);
      });
  }

  render() {
    return (
      <section className="single-post">
        <div class="col-xs-12 col-sm-12 col-md-6 col-xl-6" style="padding-left: 100px">
          <div class="row">
            <h4 class="font-weight-bold">Bank Ecosystem</h4>
            <a href="https://github.com/shawnhan108/Bank_Ecosystem" target="_blank"><button type="button" class="btn btn-primary gitbtn">Git Repo</button></a>
          </div>
          <small>Jan 2019 -  | Waterloo, ON, Canada</small>
          <ul>
            <li>A full-stack development project using Python for backend, React.js for frontend, and SQL for database management.</li>
            <li>Built on essential object-oriented programming principles that emphasize abstraction, encapsulation, inheritance, and polymorphism in our code structure.</li>
            <li>Captured the functionality of a regular banking application; Included original analytics to provide deeper insight for commercial and personal banking on an individual basis.</li>
          </ul>
        </div>
        <h1>{this.state.title}</h1>
        <h2>
          Created by {this.state.author} on {this.state.date}
        </h2>
        <div className="single-post__image">
          <Image contain imageUrl={this.state.image} />
        </div>
        <p>{this.state.content}</p>
        <h2>Shot at {this.state.location} on {this.state.taken_date}</h2>
        <h2>ISO: {this.state.ISO}</h2>
        <h2>Shutter Speed: {this.state.shutter_speed}</h2>
        <h2>Aperture: {this.state.aperture}</h2>
        <h2>Camera: {this.state.camera}</h2>
        <h2>Lens: {this.state.lens}</h2>
        <h2>Equipments: {this.state.equipment}</h2>
        <h2>Post-Editing Softwares Used: {this.state.edit_soft}</h2>
      </section>
    );
  }
}

export default SinglePost;
